                            <div class="card mb-3">
                                <div class="card-header"><strong>Status </strong></div>
                                <div class="card-body">
                                    <div class="row">
                                        <div class="col-md-12">
                                            <div class="form-group mb-0">
                                                <label class="text-muted text-capitalize d-block" for="name">Payment status </label>
                                                <label class="ui-switch ui-switch-md info m-t-xs float-left mr-3 mt-2"><input checked="" v-model="addPaymentOption.status" type="checkbox"> <i></i></label>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div class="col-md-12 text-center">
                            <button type="button" class="btn btn-default text-muted dv_cancel_submit_btn" @click="$router.push({ name: 'getPaymentOptionsByCountry' });">cancel</button>
                            <button type="button" class="btn btn-default dv_save_submit_btn" @click="savePaymentOption">save </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</template>
<script>
import Vue from 'vue'
import { mapState } from 'vuex';
import { required } from 'vuelidate/lib/validators';
import { deleteEmptyKeys } from "../../mixins";
import { ADD_PAYMENT_OPTION_BY_COUNTRY_ACTION } from '@admin/store/paymentOptionsByCountry/actions'
import { GET_COUNTRIES_LIST_ACTION } from '@admin/store/countries/actions'
export default {
    components: { },
    data() {
        return {
            baseUrl:Vue.config.baseUrl,
            baseUrlImage: Vue.config.baseUrlImage,
            addPaymentOption: {
                country_id: '',
                payment_options: [ ],
                status: true
            }
        }
    },
    validations: {
        addPaymentOption: {
            country_id: { required }
        }
    },
    computed:{
        ...mapState({
            getCountries: state => state.getCountry.getCountriesList,
        })
    },
    mounted() {
        this.getCountriesList();
    },
    methods: {
        status: function(validation) {
            if(validation != undefined){
                return {
                    error: validation.$error,
                    dirty: validation.$dirty
                }
            }
        },
        setValue: function(event) {
            this.addPaymentOption[event.target.name] = event.target.value;
            this.$v.addPaymentOption[event.target.name].$touch();
        },
        activePaymentOption: function(name) {
            if (this.addPaymentOption.payment_options.includes(name)) {
                return 'active';
            }
        },
        selectPaymentOption: function(name) {
            if (this.addPaymentOption.payment_options.includes(name)) {
                this.addPaymentOption.payment_options = this.addPaymentOption.payment_options.filter(option => option!=name);
            } else {
                this.addPaymentOption.payment_options.push(name);
            }
        },
        getCountriesList: function() {
            this.$store.dispatch('getCountry/' + GET_COUNTRIES_LIST_ACTION, {});
        },
        savePaymentOption: function() {
            let _this = this;
            _this.$v.addPaymentOption.$touch();
            if (!_this.$v.addPaymentOption.$invalid) {
                let params = deleteEmptyKeys(this.addPaymentOption);
                params['payment_options'] = JSON.stringify(params['payment_options']);
                this.$store.dispatch('getPaymentOptionsByCountry/' + ADD_PAYMENT_OPTION_BY_COUNTRY_ACTION, params).then(() => {
                    this.$router.push({ name: 'getPaymentOptionsByCountry' });
                });
            }
        }
    }
}
</script>
