    .dv_payment_option_wrapper:hover>svg.dv_selected_check_active { fill: #000000 !important; }
    .dv_payment_option_wrapper.active>svg.dv_selected_check_active { fill: #000000 !important; }
    .dv_payment_option_wrapper.active { border-color: #6e6e6e; }
    .dv_payment_option_name { text-align: center; text-transform: capitalize; display: block; padding: 15px 0 0 0; }
    .cls-1{fill:#009ee3;}.cls-1,.cls-2,.cls-3{fill-rule:evenodd;}.cls-2{fill:#113984;}.cls-3{fill:#172c70;}
    .st0{fill:#399F49;} .st1{fill:#9ACD72;} .st2{fill:#DFB28B;}
    .st0{fill:#474748;} .st1{fill:#23B053;}
    .st0installment{fill:none;stroke:#ED1D1D;stroke-width:2;stroke-miterlimit:10;} .st1installment{fill:#EC2224;}
