<script type="text/javascript">
 import Vue from 'vue'
 import { LOAD_WISH_LIST_ACTION,LOAD_SAVE_WISH_LIST_ACTION   } from '@web/store/wishlists/actions'
 import { REMOVE_CART_ITEM_ACTION,CHANGE_QTY_CART_ITEM_ACTION, SAVE_CART_ACTION, LOAD_CART_LIST_ACTION, UPDATE_CART_ACTION } from '@web/store/carts/actions'
 import { GET_COUPON_ACTION, GET_COUPONS_ACTION, VALIDATE_COUPON_ACTION } from '@web/store/coupons/actions'
 import { GET_GIFT_CERTIFICATE_ACTION, GET_GIFT_CERTIFICATES_ACTION} from '@web/store/gifts/actions'
 import { GET_PROMOTIONS_ACTION } from '@web/store/promotions/actions'
 import { mapState } from 'vuex'
 import $ from 'jquery'
 import { uuid } from 'vue-uuid'
 export default {
 	 components: {  },
 	  data() {
            return {
				baseUrl:Vue.config.baseUrl,
				baseUrlImage:Vue.config.baseUrlImage,
				getTotal:{},
				getVatData:{},
				getShippingData:{},
				uuid: '',  
				token: '',  
				coupon_code: '',
				gift_code: '',
				gift_error: '',
				coupon_error: '',
				coupon_apply_loader: false,
				gift_apply_loader: false
            }
        },
        props: {
			getCarts: {
                getCarts: Object,
                required: true,
            },
            getCartItems: {
                getCartItems: Object,
                required: true,
            },
            getWishlistData: {
                getWishlistData: Object,
                required: true,
            },
            getVat: {
                getVat: Object,
                required: true,
            },
            getShipping: {
                getShipping: Object,
                required: true,
            },
        },
        computed:{
	        	...mapState({
	             getWishLists: state => state.Wishlists.getWishLists,
	              getVats: state => state.Products.getVat,
	            //   getCarts: state => state.Carts.getCarts,
	              getCoupons: state => state.Coupons.getCoupons,
	              getCoupon: state => state.Coupons.getCoupon,
	              couponErrMessage: state => state.Coupons.getError,
	              getGifts: state => state.Gifts.getGiftCertificates,
	              getGift: state => state.Gifts.getGiftCertificate,
	              getCartQtyError: state => state.Carts.getError,
				  getPromotions: state => state.Promotion.getPromotions

	        }),
	        },
	     watch: {
            '$route': function(to, from) {
           		this.getWishListByUser();
            },
            getCartItems:[{	
		      handler: 'getCalcCart'
		    }],
		     getVat:[{
		      handler: 'getVatFunc'
		    }],
		    getShipping:[{
		      handler: 'getShippingFunc'
		    }]
          },
         methods: {
         	getLeng:function(){
                 let _this = this;
                    var currentUrl = window.location.pathname;
                    if(currentUrl != ''){
                        currentUrl = currentUrl.split('/');
                        if(currentUrl.length > 0){
                            currentUrl = currentUrl[currentUrl.length - 1];

                        }
                        if(currentUrl != ''){
                            return currentUrl;
                        }
                    }

            },
         	getCheckOut:function(){
         		let _this = this;
         		if(Object.keys(_this.$user).length > 0){
         			window.location.href=_this.baseUrl+"get-checkout-shipping/"+_this.getLeng();
         			//_this.$router.push({name:'getCheckoutShipping'}) 
         		}else{
         		$(document).ready(function() {
         		(function ($) {
		         		$('#login_register').modal('show');
		         	})(jQuery);
		         });
         		}
         	},
         	getVatFunc:function(){
         		let _this = this;
         		if(Object.keys(_this.getVat).length > 0){
         			_this.getVatData = _this.getVat;

         		}
         		
         	},
         	getShippingFunc:function(){
         		let _this = this;
         		if(Object.keys(_this.getShipping).length > 0){
					 _this.getShippingData = _this.getShipping;
					 _this.getCalcCart();
         		}
         	},
         	getCalcCart:function(){
         		let _this = this;

         		if(_this.getCartItems.length > 0){
         			let subTotal = _this.getCartItems.reduce(function (sum, obj) { return sum + obj.price*obj.quantity; }, 0);
         			let vat = _this.getCartItems.reduce(function (sum, obj) { return sum + obj.vat; }, 0);
         			//let subTotal = _this.getCartItems.reduce(function (sum, obj) { return sum + obj.price*obj.quantity; }, 0);
					 let shippingChange = ((subTotal > 200)? 0.00 :(_this.getShippingData.amount) );
         			_this.getTotal = {
         				subTotal:subTotal,
         				vat:vat,
         				deliveryChange:0.00,
         				vatName:_this.getVats.name,
         				shippingCharge:shippingChange,
         				total:(subTotal+vat+shippingChange)
         			};

					// Apply Promotion Discount
					this.getPromotionDiscount();

					// Apply Coupon in Total
					if (this.getCarts.coupon_id) {
						let coupon = this.getCarts.get_coupon;
						if (!eval(coupon['condition_amount']+coupon['coupon_condition']+this.getTotal.subTotal)) {
							this.updateCart({ cart_id: this.getCarts['id'], coupon_id: null });
							return;
						}
						_this.getTotal.coupon_code = coupon.code;
						if (coupon.discount_type == 'FIXED_PRICE_BASED') {
							_this.getTotal.coupon_discount_amount = coupon.discount;
						} else if (coupon.discount_type == 'PERCENTAGE_BASED') {
							_this.getTotal.coupon_discount_amount = (coupon.discount * _this.getTotal.total / 100);
						}
						_this.getTotal.total = _this.getTotal.total - _this.getTotal.coupon_discount_amount;
					}

					// Apply Voucher in Total
					if (this.getCarts.gift_certificate_id) {
						let gift = this.getCarts.get_gift_certificate;
						if (!eval(gift['condition_amount']+gift['logic']+this.getTotal.subTotal)) {
							this.updateCart({ cart_id: this.getCarts['id'], gift_certificate_id: null });
							return;
						}
						_this.getTotal.gift_code = gift.certificate_code;
						if (gift.certificate_discount_type == 'FIXED_PRICE_BASED') {
							_this.getTotal.gift_discount_amount = gift.discount;
						} else if (gift.certificate_discount_type == 'PERCENTAGE_BASED') {
							_this.getTotal.gift_discount_amount = (gift.discount * _this.getTotal.total / 100);
						}
						_this.getTotal.total = _this.getTotal.total - _this.getTotal.gift_discount_amount;
					}
         		}else{
         			_this.getTotal = {
                      subTotal:0.00,
                      vat:0.00,
                      vatName:0.00,
                      shippingCharge:0.00,
                      deliveryChange:0.00,
					  coupon_discount_amount: 0.00,
                      total:0.00

                    };
         		}
         		
         	},
         	addToCart:function(getProduct){

         		let _this = this;
         		this.getSession();
         		this.getToken();
         		let getProductObj = {
         			id:getProduct.id,
         			name:getProduct.name,
         			qty:1,
         			image:_this.baseUrlImage+'images/products/'+_this.getImage(getProduct),
         			session_id:_this.uuid,
         			token:_this.token,
         			price:getProduct.sale_price,
         			discount_price:getProduct.discount_price,
         			discount:getProduct.discount,
         			sku:getProduct.sku,
         			barcode:getProduct.barcode,
         			brand_id:getProduct.brand_id,
         			category_id:getProduct.category_id,
         			warranty:getProduct.warranty,
         			delivery:getProduct.delivery,
         			user_id:null,
         			vat:_this.getVats.percentage,
         		};
         		let getUser = {};
         		if(!_this.isEmpty(_this.$user)){
