			applyCoupon: function() {
				this.coupon_apply_loader = true;
				// let coupon = this.getCoupons.find(coupon => coupon.code == this.coupon_code);
				this.$store.dispatch('Coupons/' + GET_COUPON_ACTION,{ params: { 'coupon_code' : this.coupon_code } }).then(() => {
					let coupon = this.getCoupon;
					if (!coupon) {
						this.coupon_error = 'The Coupon was invalid.';
						this.coupon_apply_loader = false;
						return;
					}
					if ((coupon['accessibility']!='N') && (this.getCarts.get_applied_coupons_count >= coupon['accessibility'])) {
						this.coupon_error = 'This Coupon was expired.';
						this.coupon_apply_loader = false;
						return;
					}
					if (eval(coupon['condition_amount']+coupon['coupon_condition']+this.getTotal.subTotal)) {
						let query = [];
						query['params'] = {
							cart_id: this.getCarts['id'],
							coupon_id: coupon['id'],
							user_id: this.$user.id
						};
						this.$store.dispatch('Coupons/' + VALIDATE_COUPON_ACTION, query).then(() => {
							if (!this.couponErrMessage) {
								this.updateCart({ cart_id: this.getCarts['id'], coupon_id: coupon['id'] });
								this.coupon_code = '';
								this.coupon_error = '';
							} else {
								this.coupon_error = this.couponErrMessage;
							}
						})
					} else {
						this.coupon_error = 'Minimum value : AED. '+coupon['condition_amount'];
					}
					this.coupon_apply_loader = false;
				});
			},
			applyGift: function() {
				this.$store.dispatch('Gifts/' + GET_GIFT_CERTIFICATE_ACTION,{ params: { 'gift_code' : this.gift_code } }).then(() => {
					let gift = this.getGift;
					if (!gift) {
						this.gift_error = 'The Voucher was invalid.';
						return;
					}
					if ((gift['accessibility']!='N') && (gift.get_applied_gifts_count >= gift['accessibility'])) {
						this.gift_error = 'This Voucher was expired.';
						return;
					}
					if (eval(gift['condition_amount']+gift['logic']+this.getTotal.subTotal)) {
						this.updateCart({ cart_id: this.getCarts['id'], gift_certificate_id: gift['id'] });
						this.gift_code = '';
						this.gift_error = '';
					} else {
						this.gift_error = 'Minimum value : AED. '+gift['condition_amount'];
					}
				});
			},
			updateCart: function(params) {
				this.$store.dispatch('Carts/' + UPDATE_CART_ACTION,params).then(() => {
