        store: {
            store_name: { required },
            store_address: { required },
            store_from: { required },
            po_box_number: { required },
            company_emirates_document: { required },
            company_emirates_document_id: { required },
            company_emirates_expiry_date: { required },
            company_trade_license_document: { required },
            company_trade_license_document_id: { required },
            company_trade_license_expiry_date: { required },
        },
        aggreement: { required }
    },
    methods: {
        onFileInputChange: function(event) {
            this.store[event.target.name] = event.target.files[0];
        },
        status: function(validation) {
            if(validation != undefined){
                return {
                    error: validation.$error,
                    dirty: validation.$dirty
                }
            }
        },
        setValue: function(event, key) {
            this.$v[key][event.target.name].$touch();
        },
        saveForm: function(step,key) {
            let _this = this;
            _this.$v[key].$touch();
            if (!_this.$v[key].$invalid) {
                this.current_step=step;
                // sessionStorage.setItem("sellerRegisterStep", step);
                // this.$forceUpdate();
            }
            window.scrollTo(0,0);
        },
        submitRegistration: function() {
            let _this = this
			let params = { ..._this.seller, ..._this.store };
            if (_this.aggreement) {
				this.loaderModal('show');
				axios.post(this.baseUrl+'api/admin/seller-register',params).then(() => {
					window.location.href=this.baseUrl + 'admin/login';
				});
            }
        },
        checkCurrentStep: function() {
            return sessionStorage.getItem('sellerRegisterStep');
        },
		loaderModal: function(action) {
            if (action == 'show') {
                if (document.querySelector('.modal-backdrop')) {
                    document.getElementById("loaderModal").classList.remove('hide');
                    document.getElementById("loaderModal").classList.add('show');
                    document.querySelector("body").classList.add('modal-open');
                    document.querySelector('.modal-backdrop').classList.remove('hide');
                    document.querySelector('.modal-backdrop').classList.add('show');
                    return;
                }
                $('#loaderModal').modal('show');
            }
    
            if (action == 'hide') {
                document.getElementById("loaderModal").classList.remove('show');
                document.getElementById("loaderModal").classList.add('hide');
                document.querySelector("body").classList.remove('modal-open');
                document.querySelector('.modal-backdrop').classList.remove('show');
                document.querySelector('.modal-backdrop').classList.add('hide');
            }
        }
    }
}
</script>
<style>
#loaderModal .modal-dialog{
    display: table;
    position: relative;
    margin: 0 auto;
    top: calc(50% - 24px);
}

#loaderModal .modal-dialog .modal-content{
    background-color: transparent;
    border: none;
}
</style>