</div>
</template>
<script type="text/javascript">
 import Vue from 'vue'
 import { mapState } from 'vuex'
 import FilterProduct from '@web/components/mobile/Filter';
 import $ from "jquery";
 export default {
 	 components: { FilterProduct },
 	  data() {
            return {
              baseUrl:Vue.config.baseUrl,
              baseUrlImage:Vue.config.baseUrlImage,
              categoryName:'',
              gridSet:0,
              getQueryParam:{
              	perPage:50,
              	grid:0,
              	sortBy:'',
         		sortDir:''


              },
              
            }
        },
        props: {
            getCartItems: {
                getCartItems: Object,
                required: false,
            }, 
            getWishlist: {
                getWishlist: Object,
                required: false,
            },
        },
        computed:{
	        	...mapState({
	            getCategories: state => state.Categories.getCategories,
	            getBrands:state => state.Brand.getBrands,
	            getProductDatas: state => state.Products.getProducts,
	            getWishLists: state => state.Wishlists.getWishLists,
	            getFilters: state => state.Products.getFilters

	        }),
	        },
	         
	     watch: {
            '$route': function(to, from) {
            let _this = this;
            this.setUrlQueries();
            this.getStript();
            }
          },
         methods: {
         	getSortName:function(){
         		let _this = this;
         		if(_this.getQueryParam.sortBy == 'price'){
         			let sortDir = 'High';
         			if(_this.getQueryParam.sortDir == 'asc'){
         				sortDir = 'Low';
         			}
         			return 'Price '+((sortDir=='Low')?'High':'Low')+' to '+sortDir
         		}else if(_this.getQueryParam.sortBy == 'new_arrivals'){
         			return 'New Arrivals';
         		}else if(_this.getQueryParam.sortBy == 'recommended'){
         			return 'Recommended';
         		}else{
         			return 'sort by';
         		}
         	},
         	getPerPage:function(perPage){
         		let getQuery = this.$route.query;
         		getQuery.perPage = perPage;
         		 this.getQueryParam = getQuery;
         	},
         	resetPerPage:function(){
         		let getQuery = this.$route.query;
         		getQuery.perPage = '';
         		 this.getQueryParam = getQuery;
         		 this.getQuery();
         	},
         	getSort:function(by,dir){
         		let getQuery = this.$route.query;
         		getQuery.sortBy = by;
         		getQuery.sortDir = dir;
         		this.getQueryParam = getQuery;
         	},
         	resetSort:function(){
         		let getQuery = this.$route.query;
         		getQuery.sortBy = '';
         		getQuery.sortDir = '';
         		 this.getQueryParam = getQuery;
         		 this.getQuery();

         	},
         	getQuery:function(){
         		this.$router.replace({name: this.$route.name, query: this.getQueryParam});
         		this.closeModel();
         	},
         	closeModel:function(){
         		$(document).ready(function(){
         			(function($){
         				$('#viewmobilepageviewlist').modal('hide');
         				$('#viewmobilesortby').modal('hide');
         				$('#viewmobilefilter').modal('hide');
         				$('#viewmobileperpagelist').modal('hide');
         			})(jQuery);
         		})
         	},
         	resetGridView:function(){
         		 let getQuery = this.$route.query;
         		 getQuery.grid = 0;
         		 this.getQueryParam = getQuery;
         		 this.getQuery();
         		
         	},
         	
         	setGride:function(grid){
         		let getQuery = this.$route.query;
         		 getQuery.grid = grid;
         		 this.getQueryParam = getQuery;
         	},

         	
         	getStript:function(){
         		$(document).ready(function() {
         		(function ($) {
         		$('a.dv_per_filter_toggle_a').click( function () {
				    $(this).next().toggle('10');
				    $(this).find('svg').toggleClass('dv_per_filter_toggle_svg_90');
				});
         	})(jQuery);
         });
         	},
         	setUrlQueries:function(){
         		let getQuery = this.$route.query;
         		this.getQueryParam = getQuery;
         	}
         },
         created(){
         	this.getStript();
         },
         mounted(){
         	this.setUrlQueries();
         }

 }
</script>