<script type="text/javascript">
 import Vue from 'vue'
 import { mapState } from 'vuex'
 import $ from "jquery";
 export default {
 	 components: {  },
 	  data() {
            return {
              baseUrl:Vue.config.baseurl,
              baseUrlImage:Vue.config.baseUrlImage,
              getLocale:{},
               getProfile:{
              	first_name:'',
              	last_name:'',
              	name:'',
              	email:'',
              	dob:'',
              	mobile:'',
              	gender:'',
              	language:'',
              	image:null,
              },
            }
        },
        props: {
            getCartItems: {
                getCartItems: Object,
                required: false,
            }, 
            getWishlistData: {
                getWishlistData: Object,
                required: false,
            },
        },
         watch: {
            '$route': function(to, from) {
            let _this = this;
            
              this.getProfileUser();
            }
          },
        computed:{
	        	...mapState({
	            getCategories: state => state.Categories.getCategories,
	            getBrands:state => state.Brand.getBrands,
	        }),
	        },
         methods: {
         	setLenguage:function(){
         		
         		this.getLocale = JSON.parse(localStorage.locale);
         	},
         	getCurrentCountry:function(){
         		let getCountries = JSON.parse(localStorage.countries);
         		if(getCountries){
	         		getCountries = getCountries.get_countries.find(function(item){
	         			return item.active==1;
	         		});
	         		return getCountries.code;
         		}
         	},
         	logout:function(){
         		let _this = this;
         		if(localStorage.getItem('getLogin') != null){
         			localStorage.removeItem('getLogin');
         			_this.getUser  =  {
						              	token:'',
						              	status:0,
						              	user:{}
						              };
				window.location.href=_this.baseUrl;

         		}
         	},
         	getProfileUser:function(){
         		let _this = this;
         		if(localStorage.getItem('getLogin') != null ){
         			_this.getUser = JSON.parse(localStorage.getItem('getLogin'))
         			 _this.getProfile={
         			 	id:_this.getUser.user.id,
		              	first_name:_this.getUser.user.first_name,
		              	last_name:_this.getUser.user.last_name,
		              	name:_this.getUser.user.first_name+' '+_this.getUser.user.last_name,
		              	email:_this.getUser.user.email,
		              	dob:_this.getUser.user.dob,
		              	mobile:_this.getUser.user.mobile,
		              	gender:_this.getUser.user.gender,
		              	language:_this.getUser.user.language,
		              	profile_image:_this.getUser.user.profile_image
		              }
		             
         		}
         	},
         	getStript:function(){
         		$(document).ready(function() {
         		(function ($) {
         		$('a.dv_per_category_toggle_a').click( function () {
				    $(this).next().toggle();
				    $(this).find('svg').toggleClass('dv_per_filter_toggle_svg_90');
				});
         	})(jQuery);
         });
         	},
         },
         created(){
         	this.getStript();
         	this.getProfileUser();
         },
         mounted(){
         	
         }

 }
</script>